mapper.select:
SELECT e.employee_id, p.personal_name, p.personal_birthday, p.personal_phone_number, e.deleted_flag, e.employee_start_date, e.employee_end_date, r.room_name, pos.position_name, sy.system_email, AS is_full_documents FROM personal_info p LEFT JOIN employee e ON p.personal_info_id = e.personal_info_id LEFT JOIN system_account sy ON e.system_account_id = sy.system_account_id LEFT JOIN room r ON e.room_id = r.room_id LEFT JOIN employee_position empos ON e.employee_id = empos.employee_id LEFT JOIN position pos ON empos.position_id = pos.position_id
AND p.personal_name LIKE #{personalInfo.personalName, jdbcType=VARCHAR}
AND p.personal_phone_number = #{personalInfo.personalPhoneNumber, jdbcType=VARCHAR}
AND e.employee_start_date >= STR_TO_DATE(#{employee.employeeStartDate, jdbcType=VARCHAR}, "%Y-%m-%d")
AND e.employee_end_date <= STR_TO_DATE(#{employee.employeeEndDate, jdbcType=VARCHAR}, "%Y-%m-%d")
AND e.system_account_id = #{employee.systemAccountId, jdbcType=INTEGER}
AND r.room_id = #{room.roomId, jdbcType=INTEGER}
AND pos.position_id = #{position.positionId, jdbcType=INTEGER}
AND e.deleted_flag = #{employee.deletedFlag, jdbcType=BIT}
AND = #{isFullDocuments, jdbcType=BIT}
