mapper.update:
UPDATE request_employee


		request_status = #{requestStatus, jdbcType=VARCHAR},


		request_description = #{requestDescription, jdbcType=VARCHAR},

	updated_at = #{updatedAt, jdbcType=TIMESTAMP}

WHERE


		AND supervisor_action_id = #{supervisorActionId, jdbcType=INTEGER}


		AND approver_action_id = #{approverActionId, jdbcType=INTEGER}


		AND requester_action_id = #{requesterActionId, jdbcType=INTEGER}

	AND request_status = "WAITING"

UPDATE request_employee

	request_status = #{requestStatus, jdbcType=VARCHAR},
	updated_at = #{updatedAt, jdbcType=TIMESTAMP}

WHERE
	supervisor_action_id = #{supervisorActionId, jdbcType=INTEGER}
		AND request_status = "WAITING"

UPDATE request_employee

	request_status = #{requestStatus, jdbcType=VARCHAR},
	updated_at = #{updatedAt, jdbcType=TIMESTAMP}

WHERE
	approver_action_id = #{approverActionId, jdbcType=INTEGER}
		AND request_status = "WAITING"

mapper.select:
SELECT rt.request_type_name, () AS requester, re.request_id, re.start_date, re.end_date, re.duration, re.request_status, re.expected_approve_date, rs.reason_name, () AS approver FROM request_employee re LEFT JOIN request rq ON rq.request_id = re.request_id LEFT JOIN request_type rt ON rt.request_type_id = rq.request_type_id LEFT JOIN reason rs ON rs.reason_id = rq.reason_id LEFT JOIN approver_action apa ON apa.approver_action_id = re.approver_action_id LEFT JOIN supervisor_action sua ON sua.supervisor_action_id = re.supervisor_action_id
re.employee_id = #{requestEmployee.employeeId, jdbcType=INTEGER}
AND re.start_date >= STR_TO_DATE(#{requestEmployee.startDate, jdbcType=VARCHAR}, "%Y-%m-%d")
AND re.end_date <= STR_TO_DATE(#{requestEmployee.endDate, jdbcType=VARCHAR}, "%Y-%m-%d")
AND re.request_status = #{requestEmployee.requestStatus,jdbcType=VARCHAR}
AND rq.request_type_id = #{requestTypeId, jdbcType=INTEGER}
ORDER BY rq.request_id DESC

SELECT
	request_id,
 	request_status
FROM 
	request_employee
WHERE


		AND supervisor_action_id = #{supervisorActionId, jdbcType=INTEGER}


		AND approver_action_id = #{approverActionId, jdbcType=INTEGER}


		AND requester_action_id = #{requesterActionId, jdbcType=INTEGER}
