mapper.update:
UPDATE supervisor_action

	action_type = #{actionType, jdbcType=VARCHAR},
	updated_at = #{updatedAt, jdbcType=TIMESTAMP}

WHERE
	supervisor_action_id = #{supervisorActionId, jdbcType=INTEGER}

UPDATE supervisor_action

	action_type = #{actionType, jdbcType=VARCHAR},
	updated_at = #{updatedAt, jdbcType=TIMESTAMP}

WHERE
	supervisor_action_id = #{supervisorActionId, jdbcType=INTEGER}
		AND action_type = "WAITING"
